mapper.select:
SELECT user_id, password, table_number, authority
FROM account
WHERE user_id = #{userId}

	AND password = #{password}

mapper.update:
UPDATE menu
set
name = #{name}, category = #{category}, price = #{price}, url = #{url}, description = #{description}
WHERE menu_id = #{menuId}

mapper.select:
SELECT menu_id, name, category, price, url, description, total_star, count_star FROM menu
WHERE menu_id = #{menuId}
ORDER BY menu_id

SELECT menu_id, name, category, price, url, description, total_star, count_star
FROM menu
WHERE category = #{VALUE}

mapper.update:
UPDATE account
set
password = #{password}, table_number = #{tableNumber}
WHERE user_id = #{userId}

mapper.select:
SELECT user_id, password, table_number, authority FROM account
WHERE user_id = #{userId}
ORDER BY table_number

SELECT o.order_id, o.user_id, o.menu_id, o.team_id, o.order_time, o.count, o.star, o.is_paid, a.table_number, m.name FROM orderlist o JOIN account a ON (o.user_id = a.user_id) JOIN menu m ON (o.menu_id = m.menu_id)
WHERE order_id = #{orderId}
ORDER BY order_id DESC

SELECT o.order_id, o.user_id, o.menu_id, o.team_id, o.order_time, o.count, o.star, o.is_paid, a.table_number, m.price FROM orderlist o JOIN account a ON (o.user_id = a.user_id) JOIN menu m ON (o.menu_id = m.menu_id)
WHERE o.order_id = #{orderId}
ORDER BY is_paid,order_id

mapper.update:
UPDATE menu
SET total_star = total_star+#{star}, count_star = count_star+1
WHERE menu_id = #{menuId}

UPDATE orderlist SET is_paid = 1
, star = #{star}
WHERE user_id = #{userId} AND menu_id = #{menuId} AND team_id=#{teamId} AND is_paid = 0
